(function (Scratch) {
  "use strict";
  if (!Scratch.extensions.unsandboxed) {
    throw new Error("Recording must be run unsandboxed");
  }
  /** @type {MediaRecorder|null} */
  let mediaRecorder = null;
  let recordedChunks = [];
  const icon =
    "data:image/svg+xml;base64,";
  class Recording {
    getInfo() {
      return {
        id: "sipcrecording",
        name: "Recording",
        color1: "#696969",
        blocks: [
          {
            opcode: "startRecording",
            blockType: Scratch.BlockType.COMMAND,
            text: "Start recording",
            blockIconURI: icon,
            arguments: {},
          },
          {
            opcode: "stopRecording",
            blockType: Scratch.BlockType.COMMAND,
            text: "Stop recording",
            blockIconURI: icon,
            arguments: {},
          },
          {
            opcode: "stopRecordingAndDownload",
            blockType: Scratch.BlockType.COMMAND,
            text: "Stop recording and download with [name] as filename",
            blockIconURI: icon,
            arguments: {
              name: {
                type: Scratch.ArgumentType.STRING,
                defaultValue: "recording.wav",
              },
            },
          },
          {
            opcode: "isRecording",
            blockType: Scratch.BlockType.BOOLEAN,
            text: "Recording?",
            blockIconURI: icon,
            arguments: {},
          },
        ],
      };
    }
    async startRecording() {
      recordedChunks = [];
      if (!navigator.mediaDevices || !navigator.mediaDevices.getUserMedia) {
        console.error("The recording function is not supported by the browser");
        return;
      }
      try {
        if (!(await Scratch.canRecordAudio())) {
          throw new Error("VM denied permission");
        }
        const stream = await navigator.mediaDevices.getUserMedia({
          audio: true,
        });
        mediaRecorder = new MediaRecorder(stream);
        mediaRecorder.addEventListener("dataavailable", function (e) {
          recordedChunks.push(e.data);
        });
        mediaRecorder.start();
        console.log("Start recording");
      } catch (e) {
        console.error("Could not start recording", e);
      }
    }
    stopRecording() {
      if (!mediaRecorder) {
        console.error("Recording not started");
        return;
      }
      console.log("Stop recording");
      mediaRecorder.stop();
      mediaRecorder = null;
      recordedChunks = [];
    }
    stopRecordingAndDownload({ name }) {
      if (!mediaRecorder) {
        console.error("Recording not started");
        return;
      }
      console.log("Stop recording");
      mediaRecorder.addEventListener("stop", function () {
        const blob = new Blob(recordedChunks, { type: "audio/wav" });
        const url = URL.createObjectURL(blob);
        const downloadLink = document.createElement("a");
        downloadLink.href = url;
        downloadLink.download = name;
        document.body.appendChild(downloadLink);
        downloadLink.click();
        document.body.removeChild(downloadLink);
        URL.revokeObjectURL(url);
        recordedChunks = [];
      });
      mediaRecorder.stop();
      mediaRecorder = null;
    }
    isRecording() {
      return !!mediaRecorder;
    }
  }
  Scratch.extensions.register(new Recording());
})(Scratch);
