(function(Scratch) {
  'use strict';

  const IconURI = 'data:image/svg+xml;base64,';

  function hexToRgb(hex) {
      var result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);
      return result ? {
          r: parseInt(result[1], 16),
          g: parseInt(result[2], 16),
          b: parseInt(result[3], 16)
      } : null;
  }

  var x = document.createElement("INPUT");
  x.setAttribute("type", "color");
  x.className = "ColorExtension";
  x.hidden = false;
  document.body.appendChild(x);
  var colorPicker = document.querySelector('.ColorExtension');
  class ColorPicker {
      getInfo() {
          return {
              id: 'shovelColorPicker',
              name: 'ColorPicker',
              menuIconURI: IconURI,
              color1: '#ff7db5',
              blocks: [{
                      blockIconURI: IconURI,
                      opcode: 'showPicker',
                      blockType: Scratch.BlockType.COMMAND,
                      text: 'Show color picker',
                  },
                  {
                      blockIconURI: IconURI,
                      opcode: 'setColor',
                      blockType: Scratch.BlockType.COMMAND,
                      text: 'Set picker color to [Color]',
                      arguments: {
                          Color: {
                              type: Scratch.ArgumentType.COLOR,
                              defaultValue: '#855CD6'
                          }
                      }
                  },
                  {
                      blockIconURI: IconURI,
                      disableMonitor: true,
                      opcode: 'getColorHEX',
                      blockType: Scratch.BlockType.REPORTER,
                      text: 'Picker HEX value',
                  },
                  {
                      blockIconURI: IconURI,
                      disableMonitor: true,
                      opcode: 'getColorRGB',
                      blockType: Scratch.BlockType.REPORTER,
                      text: 'Picker [RGB] value',
                      arguments: {
                          RGB: {
                              type: Scratch.ArgumentType.STRING,
                              menu: 'RGBMenu'
                          }
                      }
                  },
              ],
              menus: {
                  RGBMenu: {
                      acceptReporters: true,
                      items: ['Red', 'Green', 'Blue']
                  }
              }
          };
      }
      setColor(args) {
          document.querySelector('.ColorExtension').value = args.Color;
      }
      getColorHEX() {
          return colorPicker.value;
      }
      showPicker() {
          colorPicker.click();
      }
      getColorRGB(args) {
          if (args.RGB == 'Red') {
              return hexToRgb(colorPicker.value).r;
          } else {
              if (args.RGB == 'Green') {
                  return hexToRgb(colorPicker.value).g;
              } else {
                  if (args.RGB == 'Blue') {
                      return hexToRgb(colorPicker.value).b;
                  } else {
                      return '';
                  }
              }
          }
      }

  }

  Scratch.extensions.register(new ColorPicker());
})(Scratch);
