  const GameJolt = (() => {
    /*!
     * This is a modified version of https://github.com/MausGames/game-jolt-api-js-library (Public Domain)
     */
    var GJAPI = {};

    GJAPI.iGameID = 0;
    GJAPI.sGameKey = "";
    GJAPI.bAutoLogin = true;

    GJAPI.sAPI = "https://api.gamejolt.com/api/game/v1_2";
    GJAPI.sLogName = "[Game Jolt API]";
    GJAPI.iLogStack = 20;

    GJAPI.asQueryParam = (function () {
      var asOutput = {};
      var asList = window.location.search.substring(1).split("&");

      // loop through all parameters
      for (var i = 0; i < asList.length; ++i) {
        // separate key from value
        var asPair = asList[i].split("=");

        // insert value into map
        if (typeof asOutput[asPair[0]] === "undefined")
          asOutput[asPair[0]] = asPair[1]; // create new entry
        else if (typeof asOutput[asPair[0]] === "string")
          asOutput[asPair[0]] = [asOutput[asPair[0]], asPair[1]];
        // extend into array
        else asOutput[asPair[0]].push(asPair[1]); // append to array
      }

      return asOutput;
    })();

    GJAPI.bOnGJ = window.location.hostname.match(/gamejolt/) ? true : false;

    GJAPI.LogTrace = function (sMessage) {
      // prevent flooding
      if (!GJAPI.iLogStack) return;
      if (!--GJAPI.iLogStack) sMessage = "(╯°□°）╯︵ ┻━┻";

      // log message and stack trace
      console.warn(GJAPI.sLogName + " " + sMessage);
      console.trace();
    };

    // ****************************************************************
    // main functions
    GJAPI.SEND_FOR_USER = true;
    GJAPI.SEND_GENERAL = false;

    GJAPI.SendRequest = function (sURL, bSendUser, pCallback) {
      // forward call to extended function
      GJAPI.SendRequestEx(sURL, bSendUser, "json", "", pCallback);
    };

    GJAPI.SendRequestEx = function (
      sURL,
      bSendUser,
      sFormat,
      sBodyData,
      pCallback
    ) {
      // add main URL, game ID and format type
      sURL =
        GJAPI.sAPI +
        encodeURI(sURL) +
        (sURL.indexOf("/?") === -1 ? "?" : "&") +
        "game_id=" +
        GJAPI.iGameID +
        "&format=" +
        sFormat;

      // add credentials of current user (for user-related operations)
      if (GJAPI.bLoggedIn && bSendUser) {
        sURL += "&username=" + GJAPI.sUserName + "&user_token=" + GJAPI.sUserToken;
      }

      // generate MD5 signature
      sURL += "&signature=" + md5(sURL + GJAPI.sGameKey);

      // send off the request
      __CreateAjax(sURL, sBodyData, function (sResponse) {
        console.info(GJAPI.sLogName + " <" + sURL + "> " + sResponse);
        if (sResponse === "" || typeof pCallback !== "function") return;

        switch (sFormat) {
          case "json":
            pCallback(eval("(" + sResponse + ")").response);
            break;

          case "dump":
            var iLineBreakIndex = sResponse.indexOf("\n");
            var sResult = sResponse.substr(0, iLineBreakIndex - 1);
            var sData = sResponse.substr(iLineBreakIndex + 1);

            pCallback({
              success: sResult === "SUCCESS",
              data: sData,
            });
            break;

          default:
            pCallback(sResponse);
        }
      });
    };

    // automatically retrieve and log in current user on Game Jolt
    GJAPI.bLoggedIn =
      GJAPI.bAutoLogin &&
        GJAPI.asQueryParam["gjapi_username"] &&
        GJAPI.asQueryParam["gjapi_token"]
        ? true
        : false;
    GJAPI.sUserName = GJAPI.bLoggedIn ? GJAPI.asQueryParam["gjapi_username"] : "";
    GJAPI.sUserToken = GJAPI.bLoggedIn ? GJAPI.asQueryParam["gjapi_token"] : "";

    // send some information to the console
    console.info(GJAPI.asQueryParam);
    console.info(
      GJAPI.sLogName +
      (GJAPI.bOnGJ ? " E" : " Not e") +
      "mbedded on Game Jolt <" +
      window.location.origin +
      window.location.pathname +
      ">"
    );
    console.info(
      GJAPI.sLogName +
      (GJAPI.bLoggedIn ? " U" : " No u") +
      "ser recognized <" +
      GJAPI.sUserName +
      ">"
    );
    if (!window.location.hostname)
      console.warn(
        GJAPI.sLogName +
        " XMLHttpRequest may not work properly on a local environment"
      );

    // ****************************************************************
    // session functions
    GJAPI.bSessionActive = true;

    GJAPI.SessionOpen = function () {
      if (!GJAPI.bLoggedIn) {
        GJAPI.LogTrace("SessionOpen() failed: no user logged in");
        return;
      }

      // check for already open session
      if (GJAPI.iSessionHandle) return;

      // send open-session request
      GJAPI.SendRequest(
        "/sessions/open/",
        GJAPI.SEND_FOR_USER,
        function (pResponse) {
          // check for success
          if (pResponse.success == "true") {
            // add automatic session ping and close
            GJAPI.iSessionHandle = window.setInterval(GJAPI.SessionPing, 30000);
            window.addEventListener("beforeunload", GJAPI.SessionClose, false);
          }
        }
      );
    };

    GJAPI.SessionPing = function () {
      if (!GJAPI.bLoggedIn) {
        GJAPI.LogTrace("SessionPing() failed: no user logged in");
        return;
      }

      // send ping-session request
      GJAPI.SendRequest(
        "/sessions/ping/?status=" + (GJAPI.bSessionActive ? "active" : "idle"),
        GJAPI.SEND_FOR_USER
      );
    };

    GJAPI.SessionClose = function () {
      if (!GJAPI.bLoggedIn) {
        GJAPI.LogTrace("SessionClose() failed: no user logged in");
        return;
      }

      if (GJAPI.iSessionHandle) {
        // remove automatic session ping and close
        window.clearInterval(GJAPI.iSessionHandle);
        window.removeEventListener("beforeunload", GJAPI.SessionClose);

        GJAPI.iSessionHandle = 0;
      }

      // send close-session request
      GJAPI.SendRequest("/sessions/close/", GJAPI.SEND_FOR_USER);
    };

    // automatically start player session
    if (GJAPI.bLoggedIn) GJAPI.SessionOpen();

    // ****************************************************************
    // user functions
    GJAPI.UserLoginManual = function (sUserName, sUserToken, pCallback) {
      if (GJAPI.bLoggedIn) {
        GJAPI.LogTrace(
          "UserLoginManual(" +
          sUserName +
          ", " +
          sUserToken +
          ") failed: user " +
          GJAPI.sUserName +
          " already logged in"
        );
        return;
      }

      // send authentication request
      GJAPI.SendRequest(
        "/users/auth/" + "?username=" + sUserName + "&user_token=" + sUserToken,
        GJAPI.SEND_GENERAL,
        function (pResponse) {
          // check for success
          if (pResponse.success == "true") {
            // save login properties
            GJAPI.bLoggedIn = true;
            GJAPI.sUserName = sUserName;
            GJAPI.sUserToken = sUserToken;

            // open session
            GJAPI.SessionOpen();
          }

          // execute nested callback
          if (typeof pCallback === "function") pCallback(pResponse);
        }
      );
    };

    GJAPI.UserLogout = function () {
      if (!GJAPI.bLoggedIn) {
        GJAPI.LogTrace("UserLogout() failed: no user logged in");
        return;
      }

      // close session
      GJAPI.SessionClose();

      // reset login properties
      GJAPI.bLoggedIn = false;
      GJAPI.sUserName = "";
      GJAPI.sUserToken = "";

      // reset trophy cache
      GJAPI.abTrophyCache = {};
    };

    GJAPI.UserFetchID = function (iUserID, pCallback) {
      // send fetch-user request
      GJAPI.SendRequest(
        "/users/?user_id=" + iUserID,
        GJAPI.SEND_GENERAL,
        pCallback
      );
    };

    GJAPI.UserFetchName = function (sUserName, pCallback) {
      // send fetch-user request
      GJAPI.SendRequest(
        "/users/?username=" + sUserName,
        GJAPI.SEND_GENERAL,
        pCallback
      );
    };

    GJAPI.UserFetchCurrent = function (pCallback) {
      if (!GJAPI.bLoggedIn) {
        GJAPI.LogTrace("UserFetchCurrent() failed: no user logged in");
        return;
      }

      // send fetch-user request
      GJAPI.UserFetchName(GJAPI.sUserName, pCallback);
    };

    // ****************************************************************
    // trophy functions
    GJAPI.abTrophyCache = {};

    GJAPI.TROPHY_ONLY_ACHIEVED = 1;
    GJAPI.TROPHY_ONLY_NOTACHIEVED = -1;
    GJAPI.TROPHY_ALL = 0;

    GJAPI.TrophyAchieve = function (iTrophyID, pCallback) {
      if (!GJAPI.bLoggedIn) {
        GJAPI.LogTrace(
          "TrophyAchieve(" + iTrophyID + ") failed: no user logged in"
        );
        return;
      }

      // check for already achieved trophy
      if (GJAPI.abTrophyCache[iTrophyID]) return;

      // send achieve-trophy request
      GJAPI.SendRequest(
        "/trophies/add-achieved/?trophy_id=" + iTrophyID,
        GJAPI.SEND_FOR_USER,
        function (pResponse) {
          // check for success
          if (pResponse.success == "true") {
            // save status
            GJAPI.abTrophyCache[iTrophyID] = true;
          }

          // execute nested callback
          if (typeof pCallback === "function") pCallback(pResponse);
        }
      );
    };

    GJAPI.TrophyFetch = function (iAchieved, pCallback) {
      if (!GJAPI.bLoggedIn) {
        GJAPI.LogTrace("TrophyFetch(" + iAchieved + ") failed: no user logged in");
        return;
      }

      // only trophies with the requested status
      var sTrophyData =
        iAchieved === GJAPI.TROPHY_ALL
          ? ""
          : "?achieved=" +
          (iAchieved >= GJAPI.TROPHY_ONLY_ACHIEVED ? "true" : "false");

      // send fetch-trophy request
      GJAPI.SendRequest("/trophies/" + sTrophyData, GJAPI.SEND_FOR_USER, pCallback);
    };

    GJAPI.TrophyFetchSingle = function (iTrophyID, pCallback) {
      if (!GJAPI.bLoggedIn) {
        GJAPI.LogTrace("TrophyFetchSingle(" + iTrophyID + ") failed: no user logged in");
        return;
      }

      // send fetch-trophy request
      GJAPI.SendRequest(
        "/trophies/?trophy_id=" + iTrophyID,
        GJAPI.SEND_FOR_USER,
        pCallback
      );
    };

    // ****************************************************************
    // score functions
    GJAPI.SCORE_ONLY_USER = true;
    GJAPI.SCORE_ALL = false;

    GJAPI.ScoreAdd = function (
      iScoreTableID,
      iScoreValue,
      sScoreText,
      sExtraData,
      pCallback
    ) {
      if (!GJAPI.bLoggedIn) {
        GJAPI.LogTrace(
          "ScoreAdd(" +
          iScoreTableID +
          ", " +
          iScoreValue +
          ", " +
          sScoreText +
          ") failed: no user logged in"
        );
        return;
      }

      // send add-score request
      GJAPI.ScoreAddGuest(
        iScoreTableID,
        iScoreValue,
        sScoreText,
        "",
        sExtraData,
        pCallback
      );
    };

    GJAPI.ScoreAddGuest = function (
      iScoreTableID,
      iScoreValue,
      sScoreText,
      sGuestName,
      sExtraData,
      pCallback
    ) {
      // use current user data or guest name
      var bIsGuest = sGuestName && sGuestName.length ? true : false;

      // send add-score request
      GJAPI.SendRequest(
        "/scores/add/" +
        "?sort=" +
        iScoreValue +
        "&score=" +
        sScoreText +
        (bIsGuest ? "&guest=" + sGuestName : "") +
        (iScoreTableID ? "&table_id=" + iScoreTableID : "") +
        (sExtraData ? "&extra_data=" + sExtraData : ""),
        bIsGuest ? GJAPI.SEND_GENERAL : GJAPI.SEND_FOR_USER,
        pCallback
      );
    };

    GJAPI.ScoreFetch = function (iScoreTableID, bOnlyUser, iLimit, pCallback) {
      if (!GJAPI.bLoggedIn && bOnlyUser) {
        GJAPI.LogTrace(
          "ScoreFetch(" +
          iScoreTableID +
          ", " +
          bOnlyUser +
          ", " +
          iLimit +
          ") failed: no user logged in"
        );
        return;
      }

      // only scores from the current user or all scores
      var bFetchAll = bOnlyUser === GJAPI.SCORE_ONLY_USER ? false : true;

      // send fetch-score request
      GJAPI.SendRequest(
        "/scores/" +
        "?limit=" +
        iLimit +
        (iScoreTableID ? "&table_id=" + iScoreTableID : ""),
        bFetchAll ? GJAPI.SEND_GENERAL : GJAPI.SEND_FOR_USER,
        pCallback
      );
    };

    // ****************************************************************
    // data store functions
    GJAPI.DATA_STORE_USER = true;
    GJAPI.DATA_STORE_GLOBAL = false;

    GJAPI.DataStoreSet = function (iStore, sKey, sData, pCallback) {
      // send set-data request
      GJAPI.SendRequest(
        "/data-store/set/?key=" + sKey + "&data=" + sData,
        iStore,
        pCallback
      );
    };

    GJAPI.DataStoreFetch = function (iStore, sKey, pCallback) {
      // send fetch-data request
      GJAPI.SendRequest(
        "/data-store/?key=" + sKey,
        iStore,
        pCallback
      );
    };

    GJAPI.DataStoreUpdate = function (iStore, sKey, sOperation, sValue, pCallback) {
      // send update-data request
      GJAPI.SendRequest(
        "/data-store/update/" +
        "?key=" +
        sKey +
        "&operation=" +
        sOperation +
        "&value=" +
        sValue,
        iStore,
        pCallback
      );
    };

    GJAPI.DataStoreRemove = function (iStore, sKey, pCallback) {
      // send remove-data request
      GJAPI.SendRequest(
        "/data-store/remove/?key=" + sKey,
        iStore,
        pCallback
      );
    };

    GJAPI.DataStoreGetKeys = function (iStore, pCallback) {
      // send get-keys request
      GJAPI.SendRequest(
        "/data-store/get-keys/",
        iStore,
        pCallback
      );
    };

    // ****************************************************************
    // create asynchronous request
    function __CreateAjax(sUrl, sBodyData, pCallback) {
      if (typeof sBodyData !== "string") sBodyData = "";

      if (window.XMLHttpRequest) {
        var pRequest = new XMLHttpRequest();

        // bind callback function
        pRequest.onreadystatechange = function () {
          if (pRequest.readyState === 4) pCallback(pRequest.responseText);
        };

        // send off the request
        if (sBodyData !== "") {
          pRequest.open("POST", sUrl);
          pRequest.setRequestHeader(
            "Content-Type",
            "application/x-www-form-urlencoded"
          );
          pRequest.send(sBodyData);
        } else {
          pRequest.open("GET", sUrl);
          pRequest.send();
        }
      } else console.error(GJAPI.sLogName + " XMLHttpRequest not supported");
    };

    GJAPI.BETTER_THAN = true;
    GJAPI.WORSE_THAN = false;
    GJAPI.FETCH_USERNAME = true;
    GJAPI.FETCH_ID = false;
    GJAPI.FETCH_ALL = true;
    GJAPI.FETCH_SINGLE = false;

    GJAPI.TimeFetch = (pCallback) => {
      GJAPI.SendRequest(
        "/time/?game_id=" + GJAPI.iGameID,
        GJAPI.SEND_GENERAL,
        pCallback
      );
    };
    GJAPI.FriendsFetch = (pCallback) => {
      if (!GJAPI.bLoggedIn) {
        GJAPI.LogTrace("FriendsFetch() failed: no user logged in");
        return;
      }
      GJAPI.SendRequest(
        "/friends/?game_id=" +
        GJAPI.iGameID +
        "&username=" +
        GJAPI.sUserName +
        "&user_token=" +
        GJAPI.sUserToken,
        GJAPI.SEND_FOR_USER,
        pCallback
      );
    };
    GJAPI.TrophyRemove = (iTrophyID, pCallback) => {
      if (!GJAPI.bLoggedIn) {
        GJAPI.LogTrace("TrophyRemove(" + iTrophyID + ") failed: no user logged in");
        return;
      }
      // Check if the trophy is not achieved
      if (!GJAPI.abTrophyCache[iTrophyID]) {
        return;
      }
      GJAPI.SendRequest(
        "/trophies/remove-achieved/?game_id=" +
        GJAPI.iGameID +
        "&username=" +
        GJAPI.sUserName +
        "&user_token=" +
        GJAPI.sUserToken +
        "&trophy_id=" +
        iTrophyID,
        GJAPI.SEND_FOR_USER,
        function (pResponse) {
          // Update trophy status if the response succeded
          if (pResponse.success == "true") {
            GJAPI.abTrophyCache[iTrophyID] = false;
          }
          if (typeof pCallback == "function") {
            pCallback(pResponse);
          }
        }
      );
    };
    GJAPI.ScoreGetRank = (iScoreTableID, iScoreValue, pCallback) => {
      GJAPI.SendRequest(
        "/scores/get-rank/?game_id=" +
        GJAPI.iGameID +
        "&sort=" +
        iScoreValue +
        "&table_id=" +
        iScoreTableID,
        GJAPI.SEND_GENERAL,
        pCallback
      );
    };
    GJAPI.ScoreGetTables = (pCallback) => {
      GJAPI.SendRequest(
        "/scores/tables/?game_id=" + GJAPI.iGameID,
        GJAPI.SEND_GENERAL,
        pCallback
      );
    };
    GJAPI.SessionCheck = (pCallback) => {
      GJAPI.SendRequest(
        "/sessions/check/?game_id=" +
        GJAPI.iGameID +
        "&username=" +
        GJAPI.sUserName +
        "&user_token=" +
        GJAPI.sUserToken,
        GJAPI.SEND_GENERAL,
        pCallback
      );
    };

    /**
     * SessionOpen and SessionClose combined
     */
    GJAPI.SessionSetStatus = (isOpen) => {
      if (!GJAPI.bLoggedIn) {
        GJAPI.LogTrace( "SessionSetStatus(" + isOpen + ") failed: no user logged in" );
        return;
      }
      GJAPI.bSessionActive = isOpen;
      if (isOpen) {
        if (GJAPI.iSessionHandle) {
          return;
        }
        GJAPI.SendRequest(
          "/sessions/open/",
          GJAPI.SEND_FOR_USER,
          function (pResponse) {
            if (pResponse.success == "true") {
              GJAPI.iSessionHandle = window.setInterval(GJAPI.SessionPing, 30000);
              window.addEventListener("beforeunload", GJAPI.SessionClose, false);
            }
          }
        );
        return;
      }
      if (GJAPI.iSessionHandle) {
        window.clearInterval(GJAPI.iSessionHandle);
        window.removeEventListener("beforeunload", GJAPI.SessionClose);
        GJAPI.iSessionHandle = 0;
      }
      GJAPI.SendRequest("/sessions/close/", GJAPI.SEND_FOR_USER);
    };

    /**
     * UserFetchName and UserFetchID combined
     * Use GJAPI.FETCH_USERNAME and GJAPI.FETCH_ID for better code readability
     */
    GJAPI.UserFetchComb = (isUsername, value, pCallback) => {
      if (isUsername) {
        GJAPI.SendRequest(
          "/users/?username=" + value,
          GJAPI.SEND_GENERAL,
          pCallback
        );
        return;
      }
      GJAPI.SendRequest("/users/?user_id=" + value, GJAPI.SEND_GENERAL, pCallback);
    };

    /**
     * ScoreFetch but with better_than and worse_than parameters
     * Use GJAPI.BETTER_THAN and GJAPI.WORSE_THAN for better code readability
     * If value is set to 0 it will work like riginal ScoreFetch
     */
    GJAPI.ScoreFetchEx = (
      iScoreTableID,
      bOnlyUser,
      iLimit,
      betterOrWorse,
      value,
      pCallback
    ) => {
      if (!GJAPI.bLoggedIn && bOnlyUser) {
        GJAPI.LogTrace(
          "ScoreFetch(" +
          iScoreTableID +
          ", " +
          bOnlyUser +
          ", " +
          iLimit +
          ", " +
          betterOrWorse +
          ", " +
          value +
          ") failed: no user logged in"
        );
        return;
      }
      var bFetchAll = bOnlyUser == GJAPI.SCORE_ONLY_USER ? false : true;
      GJAPI.SendRequest(
        "/scores/" +
        "?limit=" +
        iLimit +
        (iScoreTableID ? "&table_id=" + iScoreTableID : "") +
        (betterOrWorse ? "&better_than=" : "&worse_than=") +
        value,
        bFetchAll ? GJAPI.SEND_GENERAL : GJAPI.SEND_FOR_USER,
        pCallback
      );
    };

    /**
     * Unused in the extension because of ScoreFetchGuestEx
     */
    GJAPI.ScoreFetchGuest = (iScoreTableID, name, iLimit, pCallback) => {
      GJAPI.SendRequest(
        "/scores/" +
        "?limit=" +
        iLimit +
        (iScoreTableID ? "&table_id=" + iScoreTableID : "") +
        "&guest=" +
        name,
        GJAPI.SEND_GENERAL,
        pCallback
      );
    };

    /**
     * ScoreFetchGuest but with better_than and worse_than parameters
     * Use GJAPI.BETTER_THAN and GJAPI.WORSE_THAN for better code readability
     * If value is set to 0 it will work like original ScoreFetchGuest
     */
    GJAPI.ScoreFetchGuestEx = (
      iScoreTableID,
      name,
      iLimit,
      betterOrWorse,
      value,
      pCallback
    ) => {
      GJAPI.SendRequest(
        "/scores/" +
        "?limit=" +
        iLimit +
        (iScoreTableID ? "&table_id=" + iScoreTableID : "") +
        "&guest=" +
        name +
        (betterOrWorse ? "&better_than=" : "&worse_than=") +
        value,
        GJAPI.SEND_GENERAL,
        pCallback
      );
    };

    /**
     * TrophyFetch and TrophyFetchSingle combined
     * Use GJAPI.FETCH_ALL and GJAPI.FETCH_SINGLE for better code readability
     */
    GJAPI.TrophyFetchComb = (isAll, value, pCallback) => {
      if (!GJAPI.bLoggedIn) {
        GJAPI.LogTrace("TrophyFetchComb(" + isAll + ", " + value + ") failed: no user logged in");
        return;
      }
      if (isAll) {
        var sTrophyData =
          value === GJAPI.TROPHY_ALL
            ? ""
            : "?achieved=" +
            (value >= GJAPI.TROPHY_ONLY_ACHIEVED ? "true" : "false");
        GJAPI.SendRequest(
          "/trophies/" + sTrophyData,
          GJAPI.SEND_FOR_USER,
          pCallback
        );
        return;
      }
      GJAPI.SendRequest(
        "/trophies/?trophy_id=" + value,
        GJAPI.SEND_FOR_USER,
        pCallback
      );
    };

    /**
     * Modified UserLoginManual to login users automatically if their username and token are detected
     */
    GJAPI.UserLoginAuto = pCallback => {
      if (!GJAPI.bOnGJ) {
        GJAPI.LogTrace("UserLoginAuto() failed: No username or token detected");
        return;
      }
      if (GJAPI.bLoggedIn) { GJAPI.LogTrace("UserLoginAuto() failed: user " + GJAPI.sUserName + " already logged in"); return; }
      GJAPI.SendRequest("/users/auth/" + "?username=" + GJAPI.asQueryParam["gjapi_username"] +
        "&user_token=" + GJAPI.asQueryParam["gjapi_token"],
        GJAPI.SEND_GENERAL,
        function (pResponse) {
          if (pResponse.success == "true") {
            GJAPI.bLoggedIn = true;
            GJAPI.sUserName = GJAPI.asQueryParam["gjapi_username"];
            GJAPI.sUserToken = GJAPI.asQueryParam["gjapi_token"];
            GJAPI.SessionOpen();
          }
          if (typeof pCallback === "function") {
            pCallback(pResponse);
          }
        });
    };

    /**
     * DataStoreGetKeys but with a pattern parameter
     * The placeholder character for patterns is *
     */
    GJAPI.DataStoreGetKeysEx = (iStore, pattern, pCallback) => {
      GJAPI.SendRequest("/data-store/get-keys/?pattern=" + pattern, iStore, pCallback);
    };

    return GJAPI;
  })();

