  math_3d
    .addBlock(
      "V3: rotate [a] around [b] by yaw:[yaw] pitch:[pitch], and roll:[roll]",
      "rotateAroundPointV3",
      Scratch.BlockType.REPORTER,
      ({ a, b, yaw, pitch, roll }) => {
        a = JSON.parse(a);
        b = JSON.parse(b);

        a[0] -= b[0];
        a[1] -= b[1];
        a[2] -= b[2];

        const sinAndCos = [
          Math.sin(yaw * d2r),
          Math.cos(yaw * d2r),
          Math.sin(pitch * d2r),
          Math.cos(pitch * d2r),
          Math.sin(roll * d2r),
          Math.cos(roll * d2r),
        ];

        let temp = a[0];

        a[0] = a[2] * sinAndCos[0] + a[0] * sinAndCos[1];
        a[2] = a[2] * sinAndCos[1] - temp * sinAndCos[0];

        temp = a[1];

        a[1] = a[2] * sinAndCos[2] + a[1] * sinAndCos[3];
        a[2] = a[2] * sinAndCos[3] - temp * sinAndCos[2];

        temp = a[0];

        a[0] = a[1] * sinAndCos[4] + a[0] * sinAndCos[5];
        a[1] = a[1] * sinAndCos[5] - temp * sinAndCos[4];

        a[0] += b[0];
        a[1] += b[1];
        a[2] += b[2];

        return JSON.stringify(a);
      }
    )
    .addArgument("a", "[0,0,0]")
    .addArgument("b", "[0,0,0]")
    .addArgument("yaw", "0")
    .addArgument("pitch", "0")
