  math_3d.addLabel("camera");

  math_3d
    .addBlock(
      "set camera position to [a]",
      "cam3DsetPosition",
      Scratch.BlockType.COMMAND,
      ({ a }) => {
        a = JSON.parse(a);

        if (a) {
          camera.position = a;
        }
      }
    )
    .addArgument("a", "[0,0,0]");

  math_3d.addBlock(
    "get camera position",
    "cam3DgetPosition",
    Scratch.BlockType.REPORTER,
    () => {
      return JSON.stringify(camera.position);
    }
  );

  math_3d
    .addBlock(
      "set camera rotation to [a]",
      "cam3DsetRotation",
      Scratch.BlockType.COMMAND,
      ({ a }) => {
        a = JSON.parse(a);

        if (a) {
          camera.rotation = a;
        }
      }
    )
    .addArgument("a", "[0,0,0]");

  math_3d.addBlock(
    "get camera rotation",
    "cam3DgetRotation",
    Scratch.BlockType.REPORTER,
    () => {
      return JSON.stringify(camera.rotation);
    }
  );

  math_3d.addLabel("sprite 3D");

  math_3d
    .addBlock(
      "set fov to [dist]",
      "setFov",
      Scratch.BlockType.COMMAND,
      ({ dist }) => {
        fov = dist;
      }
    )
    .addArgument("dist", 300);

  math_3d.checkFor3dPositionData = (targetID) => {
    if (!spritePositions[targetID]) {
      spritePositions[targetID] = [0, 0, fov];
    }
  };

  math_3d
    .addBlock(
      "set my position to [a]",
      "spr3DsetPosition",
      Scratch.BlockType.COMMAND,
      ({ a }, util) => {
        const target = util.target;
        math_3d.checkFor3dPositionData(target.id);

        a = JSON.parse(a);

        spritePositions[target.id] = a;
      }
    )
    .addArgument("a", "[0,0,0]")
    .setFilter();

  math_3d
    .addBlock(
      "change my position by [a]",
      "spr3DchangePosition",
      Scratch.BlockType.COMMAND,
      ({ a }, util) => {
        const target = util.target;
        math_3d.checkFor3dPositionData(target.id);

        a = JSON.parse(a);

        spritePositions[target.id][0] += a[0];
        spritePositions[target.id][1] += a[1];
        spritePositions[target.id][2] += a[2];
      }
    )
    .addArgument("a", "[0,0,0]")
    .setFilter();

  math_3d
    .addBlock(
      "my 3d position",
      "spr3DgetPosition",
      Scratch.BlockType.REPORTER,
      (args, util) => {
        const target = util.target;
        math_3d.checkFor3dPositionData(target.id);
        return JSON.stringify(spritePositions[target.id]);
      }
    )
    .setFilter();

  math_3d
    .addBlock(
      "go to my position in 3D",
      "spr3D",
      Scratch.BlockType.COMMAND,
      (args, util) => {
        const target = util.target;
        math_3d.checkFor3dPositionData(target.id);
        const myData = JSON.parse(JSON.stringify(spritePositions[target.id]));

        myData[0] -= camera.position[0];
        myData[1] -= camera.position[1];
        myData[2] -= camera.position[2];

        const sinAndCos = [
          Math.sin(-camera.rotation[0] * d2r),
          Math.cos(-camera.rotation[0] * d2r),
          Math.sin(-camera.rotation[1] * d2r),
          Math.cos(-camera.rotation[1] * d2r),
          Math.sin(-camera.rotation[2] * d2r),
          Math.cos(-camera.rotation[2] * d2r),
        ];

        let temp = myData[0];

        myData[0] = myData[2] * sinAndCos[0] + myData[0] * sinAndCos[1];
        myData[2] = myData[2] * sinAndCos[1] - temp * sinAndCos[0];

        temp = myData[1];

        myData[1] = myData[2] * sinAndCos[2] + myData[1] * sinAndCos[3];
        myData[2] = myData[2] * sinAndCos[3] - temp * sinAndCos[2];

        temp = myData[0];

        myData[0] = myData[1] * sinAndCos[4] + myData[0] * sinAndCos[5];
        myData[1] = myData[1] * sinAndCos[5] - temp * sinAndCos[4];

        let project = fov / myData[2];

        if (myData[2] < 1) {
          target.setVisible(false);
        } else {
          target.setVisible(true);
          target.setSize(100 * project);
          target.setXY(myData[0] * project, myData[1] * project);
        }
      }
    )
    .setFilter();

  //Bad block enable at your own risk!
