// Name: Matrix
// ID: qxsckmatrix
// Description: Help you calculate and operate matrix.
// By: qxsck

(function (Scratch) {
  "use strict";
  const Icon =
    "data:image/svg+xml;base64,";

  class Matrix {
    constructor() {
      this.getmatrix = (n, m) =>
        Array.from({ length: n }, () => new Array(m).fill(0));

      this.isMatrix = function (matrix) {
        if (!Array.isArray(matrix)) return false;
        const row = matrix.length,
          col = matrix[0].length;
        if (row === 0 || col === 0) return false;
        for (let i = 0; i < row; i++) {
          if (!Array.isArray(matrix[i]) || matrix[i].length !== col)
            return false;
          for (let j = 0; j < col; j++) {
            if (Number.isNaN(Number(matrix[i][j]))) return false;
          }
        }
        return true;
      };
      this.addMatrix = function (matrix1, matrix2) {
        if (!this.isMatrix(matrix1) || !this.isMatrix(matrix2)) return "[]";
        matrix1 = matrix1.map((row) => row.map((val) => Number(val)));
        matrix2 = matrix2.map((row) => row.map((val) => Number(val)));

        const row = matrix1.length,
          col = matrix1[0].length;
        if (row !== matrix2.length || col !== matrix2[0].length) return "[]";
        let result = this.getmatrix(row, col);
        result = result.map((row, i) =>
          row.map((val, j) => matrix1[i][j] + matrix2[i][j])
        );
        return result;
      };
      this.subMatrix = function (matrix1, matrix2) {
        if (!this.isMatrix(matrix1) || !this.isMatrix(matrix2)) return "[]";
        matrix1 = matrix1.map((row) => row.map((val) => Number(val)));
        matrix2 = matrix2.map((row) => row.map((val) => Number(val)));

        const row = matrix1.length,
          col = matrix1[0].length;
        if (row !== matrix2.length || col !== matrix2[0].length) return "[]";
        let result = this.getmatrix(row, col);
        result = result.map((row, i) =>
          row.map((val, j) => matrix1[i][j] - matrix2[i][j])
        );
        return result;
      };
      this.mulMatrix = function (matrix1, matrix2) {
        if (!this.isMatrix(matrix1) || !this.isMatrix(matrix2)) return "[]";
        matrix1 = matrix1.map((row) => row.map((val) => Number(val)));
        matrix2 = matrix2.map((row) => row.map((val) => Number(val)));
        let row = matrix1.length;
        if (matrix1[0].length !== matrix2.length) return "[]";
        let result = this.getmatrix(row, matrix2[0].length);
        for (let i = 0; i < row; i++) {
          for (let j = 0; j < matrix2[0].length; j++) {
            let sum = 0;
            for (let k = 0; k < matrix2.length; k++) {
              sum += matrix1[i][k] * matrix2[k][j];
            }
            result[i][j] = sum;
          }
        }
        return result;
      };
      this.scalarMatrix = function (matrix1, num) {
        if (!this.isMatrix(matrix1)) return "[]";
        matrix1 = matrix1.map((row) => row.map((val) => Number(val)));

        const row = matrix1.length,
          col = matrix1[0].length;
        let result = this.getmatrix(row, col);
        result = result.map((row, i) =>
          row.map((val, j) => matrix1[i][j] * num)
        );
        return result;
      };
      this.transposeMatrix = function (matrix1) {
        if (!this.isMatrix(matrix1)) return "[]";
        matrix1 = matrix1.map((row) => row.map((val) => Number(val)));

        const row = matrix1.length,
          col = matrix1[0].length;
        let result = this.getmatrix(col, row);
        result = result.map((row, i) => row.map((val, j) => matrix1[j][i]));
        return result;
      };
      this.traceMatrix = function (matrix1) {
        if (!this.isMatrix(matrix1)) return "NaN";
        matrix1 = matrix1.map((row) => row.map((val) => Number(val)));

        const row = matrix1.length,
          col = matrix1[0].length;
        if (row === col) {
          let result = 0;
          for (let i = 0; i < row; i++) result += matrix1[i][i];
          return result;
        } else {
          return "NaN";
        }
      };
    }

    getInfo() {
      return {
        id: "qxsckmatrix",
        name: Scratch.translate("Matrix"),
        color1: "#6caa0e",
        color2: "#6caa0e",
        blockIconURI: Icon,
        menuIconURI: Icon,
        blocks: [
          //command,reporter,Boolean,hat,conditional,loop
          {
            opcode: "isAMatrix",
            blockType: "Boolean",
            text: Scratch.translate("[ARR] is matrix?"),
            arguments: {
              ARR: {
                type: "string",
                defaultValue: "[[1,2],[3,4]]",
              },
            },
          },
          {
            opcode: "matrixOper",
            blockType: "reporter",
            text: Scratch.translate("matrix [ARR] [OPER] matrix [ARR2]"),
            arguments: {
              ARR: {
                type: "string",
                defaultValue: "[[1,2],[3,4]]",
              },
              ARR2: {
                type: "string",
                defaultValue: "[[1,2],[3,4]]",
              },
              OPER: {
                type: "string",
                menu: "matrixOper.List",
              },
            },
          },
          {
            opcode: "matrixScalar",
            blockType: "reporter",
            text: Scratch.translate("matrix [ARR] * [NUM]"),
            arguments: {
              ARR: {
                type: "string",
                defaultValue: "[[1,2],[3,4]]",
              },
              NUM: {
                type: "string",
                defaultValue: "2",
              },
            },
          },
          {
            opcode: "matrixTranspose",
            blockType: "reporter",
            text: Scratch.translate("transpose matrix [ARR]"),
            arguments: {
              ARR: {
                type: "string",
                defaultValue: "[[1,2],[3,4]]",
              },
            },
          },
          {
            opcode: "matrixTrace",
            blockType: "reporter",
            text: Scratch.translate("trace of matrix [ARR]"),
            arguments: {
              ARR: {
                type: "string",
                defaultValue: "[[1,2],[3,4]]",
              },
            },
          },
        ],
        menus: {
          "matrixOper.List": [
            {
              text: "+",
              value: "+",
            },
            {
              text: "-",
              value: "-",
            },
            {
              text: "*",
              value: "*",
            },
          ],
        },
      };
    }
    isAMatrix(args) {
      try {
        let arr = JSON.parse(args.ARR);
        return this.isMatrix(arr);
      } catch (error) {
        return false;
      }
    }
    matrixOper(args) {
      let oper = args.OPER;
      try {
        let arr = JSON.parse(args.ARR),
          arr2 = JSON.parse(args.ARR2);
        if (oper === "+") return JSON.stringify(this.addMatrix(arr, arr2));
        else if (oper === "-") return JSON.stringify(this.subMatrix(arr, arr2));
        else if (oper === "*") return JSON.stringify(this.mulMatrix(arr, arr2));
      } catch (error) {
        return "[]";
      }
    }
    matrixScalar(args) {
      try {
        let arr = JSON.parse(args.ARR),
          num = Number(args.NUM);
        return JSON.stringify(this.scalarMatrix(arr, num));
      } catch (error) {
        return "[]";
      }
    }
    matrixTranspose(args) {
      try {
        let arr = JSON.parse(args.ARR);
        return JSON.stringify(this.transposeMatrix(arr));
      } catch (error) {
        return "[]";
      }
    }
    matrixTrace(args) {
      try {
        let arr = JSON.parse(args.ARR);
        return this.traceMatrix(arr);
      } catch (error) {
        return "NaN";
      }
    }
  }
  Scratch.extensions.register(new Matrix());
})(Scratch);
