// Name: Couplers
// ID: truefantomcouplers
// Description: A few adapter blocks.
// By: TrueFantom <https://scratch.mit.edu/users/TrueFantom/>

((Scratch) => {
  "use strict";

  const icon =
    "data:image/svg+xml;base64,";

  const cast = Scratch.Cast;

  class Couplers {
    getInfo() {
      return {
        id: "truefantomcouplers",
        name: "Couplers",

        color1: "#404040",

        menuIconURI: icon,

        blocks: [
          {
            opcode: "value1_or_value2_block",
            blockType: Scratch.BlockType.REPORTER,
            text: "[BOOLEAN] ? [VALUE1] : [VALUE2]",
            arguments: {
              BOOLEAN: {
                type: Scratch.ArgumentType.BOOLEAN,
              },
              VALUE1: {
                type: Scratch.ArgumentType.STRING,
                defaultValue: "apple",
              },
              VALUE2: {
                type: Scratch.ArgumentType.STRING,
                defaultValue: "banana",
              },
            },
          },
          {
            opcode: "boolean_block",
            blockType: Scratch.BlockType.BOOLEAN,
            text: "[MENU]",
            arguments: {
              MENU: {
                type: Scratch.ArgumentType.STRING,
                menu: "boolean_menu",
              },
            },
          },
          {
            opcode: "value_in_boolean_block",
            blockType: Scratch.BlockType.BOOLEAN,
            text: "[VALUE]",
            arguments: {
              VALUE: {
                type: Scratch.ArgumentType.STRING,
                defaultValue: "apple",
              },
            },
          },
          {
            opcode: "value_in_string_block",
            blockType: Scratch.BlockType.REPORTER,
            text: "[VALUE]",
            arguments: {
              VALUE: {
                type: Scratch.ArgumentType.STRING,
                defaultValue: "apple",
              },
            },
          },
          {
            opcode: "true_block",
            blockType: Scratch.BlockType.BOOLEAN,
            text: "true",
            hideFromPalette: true,
            disableMonitor: true,
          },
          {
            opcode: "false_block",
            blockType: Scratch.BlockType.BOOLEAN,
            text: "false",
            hideFromPalette: true,
            disableMonitor: true,
          },

          "---",

          {
            opcode: "color_block",
            blockType: Scratch.BlockType.REPORTER,
            text: "color [COLOUR]",
            arguments: {
              COLOUR: {
                type: Scratch.ArgumentType.COLOR,
                defaultValue: "#ff0000",
              },
            },
          },
          {
            opcode: "angle_block",
            blockType: Scratch.BlockType.REPORTER,
            text: "angle [ANGLE]",
            arguments: {
              ANGLE: {
                type: Scratch.ArgumentType.ANGLE,
                defaultValue: "90",
              },
            },
          },
          {
            opcode: "matrix_block",
            blockType: Scratch.BlockType.REPORTER,
            text: "matrix [MATRIX]",
            arguments: {
              MATRIX: {
                type: Scratch.ArgumentType.MATRIX,
                defaultValue: "0101001010000001000101110",
              },
            },
          },
          {
            opcode: "note_block",
            blockType: Scratch.BlockType.REPORTER,
            text: "note [NOTE]",
            arguments: {
              NOTE: {
                type: Scratch.ArgumentType.NOTE,
                defaultValue: "",
              },
            },
          },
        ],
        menus: {
          boolean_menu: {
            acceptReporters: true,
            items: ["true", "false", "random"],
          },
        },
      };
    }

    value1_or_value2_block({ BOOLEAN, VALUE1, VALUE2 }) {
      return cast.toBoolean(BOOLEAN) ? VALUE1 : VALUE2;
    }
    boolean_block({ MENU }) {
      const menu = cast.toString(MENU).toLowerCase();
      switch (menu) {
        case "true":
          return true;
        case "false":
          return false;
        case "random":
        default:
          return Math.random() < 0.5;
      }
    }
    value_in_boolean_block({ VALUE }) {
      return VALUE;
    }
    value_in_string_block({ VALUE }) {
      return VALUE;
    }
    true_block() {
      return true;
    }
    false_block() {
      return false;
    }
    color_block(args) {
      return args.COLOUR;
    }
    angle_block(args) {
      return args.ANGLE;
    }
    matrix_block(args) {
      return args.MATRIX;
    }
    note_block(args) {
      return args.NOTE;
    }
  }

  Scratch.extensions.register(new Couplers());
})(Scratch);
