((Scratch) => {
  "use strict";

  const icon =
    "data:image/svg+xml;base64,";

  class Network {
    getInfo() {
      return {
        id: "truefantomnetworkm",
        name: "Network",

        color1: "#83cc70",
        color2: "#76b865",
        color3: "#69a35a",

        menuIconURI: icon,

        blocks: [
          {
            opcode: "connected_to_internet_block",
            blockType: Scratch.BlockType.BOOLEAN,
            text: "connected to internet?",
          },
          "---",
          {
            opcode: "current_url_block",
            blockType: Scratch.BlockType.REPORTER,
            text: "current url",
          },
          "---",
          {
            opcode: "network_type_block",
            blockType: Scratch.BlockType.REPORTER,
            text: "network type",
          },
          {
            opcode: "network_generation_block",
            blockType: Scratch.BlockType.REPORTER,
            text: "network generation",
          },
          "---",
          {
            opcode: "downlink_speed_block",
            blockType: Scratch.BlockType.REPORTER,
            text: "downlink speed in mb/s",
          },
          {
            opcode: "downlink_max_speed_block",
            blockType: Scratch.BlockType.REPORTER,
            text: "downlink max speed in mb/s",
          },
          {
            opcode: "rtt_block",
            blockType: Scratch.BlockType.REPORTER,
            text: "rtt in ms",
          },
        ],
      };
    }

    connected_to_internet_block() {
      try {
        return navigator.onLine;
      } catch (err) {
        return false;
      }
    }
    current_url_block() {
      try {
        return document.URL || "";
      } catch (err) {
        return "";
      }
    }
    network_type_block() {
      try {
        switch (navigator.connection.type) {
          case "bluetooth":
            return "bluetooth";
          case "cellular":
            return "cellular";
          case "ethernet":
            return "ethernet";
          case "wifi":
            return "wi-fi";
          case "wimax":
            return "wimax";
          default:
            return "";
        }
      } catch (err) {
        return "";
      }
    }
    network_generation_block() {
      try {
        switch (navigator.connection.effectiveType) {
          case "slow-2g":
          case "2g":
            return "2g";
          case "3g":
            return "3g";
          case "4g":
            return "4g";
          default:
            return "";
        }
      } catch (err) {
        return "";
      }
    }
    downlink_speed_block() {
      try {
        return navigator.connection.downlink || "";
      } catch (err) {
        return "";
      }
    }
    downlink_max_speed_block() {
      try {
        return navigator.connection.downlinkMax || "";
      } catch (err) {
        return "";
      }
    }
    rtt_block() {
      try {
        return navigator.connection.rtt || "";
      } catch (err) {
        return "";
      }
    }
  }

  Scratch.extensions.register(new Network());
})(Scratch);
